mapper.select:
SELECT * FROM USERS
WHERE MATCH (email,name) AGAINST (CONCAT(#{keyword},'*') IN BOOLEAN MODE)
WHERE 1
AND id IN (SELECT u.id FROM USERS u INNER JOIN USER_ROLE ur ON ur.fk_userId = u.id INNER JOIN ROLE r on ur.fk_roleId = r.roleId WHERE r.role IN
#{item}
)

SELECT u.`name`, u.`surname`, u.`birthday`, u.`gender`, u.`email`, u.`phoneNumber`, u.`provider`, p.url, u.`identifier` as imageUrl FROM USERS u
INNER JOIN PHOTO p ON u.fk_photoId = p.photoId
WHERE identifier=#{identifier} AND provider=#{provider}

mapper.insert:
INSERT INTO `USERS`(`identifier`, `name`, `surname`, `birthday`, `gender`, `email`, `fk_photoId`, `fk_locale`, `password`, `provider`, `refreshToken`)
VALUES (#{user.identifier},#{user.name},NULL,(STR_TO_DATE(#{user.birthday},'%d/%m/%Y')),NULL,#{user.email},NULL,NULL,#{user.password},#{user.provider},#{user.refreshToken});
SET @userInsertedId = (SELECT LAST_INSERT_ID());

    INSERT INTO `USER_ROLE`(`fk_userId`, `fk_roleId`) VALUES (@userInsertedId,#{roleId});

mapper.select:
SELECT r.roleId, r.role, r.description FROM ROLE r
INNER JOIN USER_ROLE ur ON ur.fk_roleId = r.roleId
WHERE ur.fk_userId = #{user.id}
