mapper.insert:
INSERT INTO `USERS`(`identifier`, `name`, `surname`, `birthday`, `gender`, `email`, `fk_photoId`, `fk_locale`, `password`, `provider`, `phoneNumber`, `refreshToken`)
VALUES (#{user.identifier},#{user.name},NULL,NULL,NULL,#{user.email},NULL,NULL,NULL,#{user.provider},NULL,#{user.refreshToken});
SET @userInsertedId = (SELECT LAST_INSERT_ID());

    INSERT INTO `USER_ROLE`(`fk_userId`, `fk_roleId`) VALUES (@userInsertedId,#{roleId});
