mapper.select:

        SELECT * FROM PLACE WHERE MATCH (name,description,city,country) AGAINST (CONCAT(#{keyword},'*') IN BOOLEAN MODE)



        SELECT * FROM PLACE WHERE 1



            AND isVerified=0


            AND isPublic=0



        AND placeId IN (SELECT cp.fk_placeId FROM CATEGORY c INNER JOIN CATEGORY_PLACE cp ON c.categoryId =
        cp.fk_categoryId
        WHERE c.name IN

            #{item}
        )


            AND timeAdded BETWEEN #{filterDateAdded[0]} AND #{filterDateAdded[1]}


        AND dateModified BETWEEN #{filterDateModified[0]} AND #{filterDateModified[1]}

ORDER BY dateModified DESC

mapper.update:
UPDATE PLACE SET county=#{p.county}, municipality=#{p.municipality}, averageTimeSpent=#{p.averageTimeSpent}, price=#{p.price}, name=#{p.name}, description=#{p.description}, city=#{p.city}, country=#{p.country}, latitude=#{p.latitude},
longitude=#{p.longitude}, address=#{p.address}, phoneNumber=#{p.phoneNumber}, website=#{p.website}, hasSchedule=#{p.hasSchedule}, isPublic=#{p.isPublic}, isVerified=#{p.isVerified}
WHERE placeId=#{p.placeId}
